mapper.insert:
INSERT INTO tbl_article (
	article_no
	, title
	, content
	, writer
	, regdate
	, viewcnt
) VALUES (
	#{article_no}
	, #{title}
	, #{content}
	, #{writer}
	, now()
	, #{viewCnt}
)

mapper.update:
UPDATE tbl_article
SET
	title = #{title}
	, content = #{content}
WHERE
	article_no = #{article_no}
